import React from 'react';
/* import logo from './logo.svg';
 */import './App.css';
import axios from 'axios';
import AuthorList from './components/Author';
import AuthorItem from './components/Author';
import UserList from './components/users';
/*import ProjectList from './components/project_app';
import ProjectItem from "./components/project_app";
import TodoList from './components/todo';  */
import {BrowserRouter, Link, /* Navigate ,*/ Route, Routes} from "react-router-dom";
/* import NotFound404 from "./components/NotFound404";
 */


class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      'authors': [],
      'users': [],
    }
  }
  componentDidMount() {
    axios.get('http://127.0.0.1:8000/api/users')
      .then(response => {
        const users = response.data
          this.setState(
          {
          'users': users,
          }
        )
      }).catch(error => console.log(error))
    axios.get('http://127.0.0.1:8000/api/authors')
      .then(response => {
        const author = response.data
        this.setState(
          {
            'authors': author,
          }
        )
      }).catch(error => console.log(error))
  }

  render() {
    return (
      <div className="App">
        <BrowserRouter>
          <nav>
            <li>
              <Link to='/author'>Authors</Link>
            </li>
          </nav>

          <Routes>
            <Route path='/author'>
              <Route index element={<AuthorList author={this.state.author}/>}/>
              <Route path=':firstname' element={<AuthorItem author={this.state.author}/>}/>
            </Route>
          </Routes>
        </BrowserRouter>
       </div>
    )
  }
}

export default App;
