import * as React from "react";
import { Link } from "react-router-dom";
import AuthorList from "./Author";
import BooksList from "./books";
import ProjectList from "./project_app";
import TodoList from "./todo";

const MenuItem = ({menu/* , BooksList, ProjectList, TodoList */}) => {
    return (
        <tr>
            <td>
                <Link to={`/Author/${AuthorList.last_name}`}>{AuthorList.last_name}</Link>
            </td>
            <td>
                <Link to={`/books/${BooksList.name}`}>{BooksList.name}</Link>
            </td><td>   
                <Link to={`/project_app/${ProjectList.name}`}>{ProjectList.name}</Link>
            </td><td>
                <Link to={`/todo/${TodoList.text}`}>{TodoList.text}</Link>
            </td>
        </tr>
    )
}

const MenuList = (menu) => {
    return (
        <table>
            <thead>
                <th>
               |Имя автора
                </th>
               {/*  <th>
                | Название книги
                </th>
                <th>
                | Проект
                </th>
                <th>
                | Заметки|
                </th> */}
                    {menu.map((menu) => (<MenuItem menu={this.state.menu} />))}
            </thead>
            
        </table>
    )
}

export default MenuList
