import React from 'react'
import {Link} from "react-router-dom";



const AuthorItem = ({author}) => {
    return (
        <tr>
            <td>
                {author.id}
            </td>
            <td>
                <Link to={AuthorIndexPage}>{author.first_name}</Link>
            </td>
            <td>
                {author.last_name}
            </td>
            <td>
                {author.birthday_year}
            </td>
        </tr>
    )
}

function AuthorIndexPage({ author }) {
  return (
    <div>
      <ul>
        {author.map((author) => (
          <li key={author.id}>
            <Link to={author.id}>{author.name}</Link>
          </li>
        ))}
      </ul>
    </div>
  );
}

const AuthorList = ({ author }) => {
    return (
        <table>
            <th>
                id автора
            </th>
            <th>
                Имя автора
            </th>
            <th>
                Фамилимя автора
            </th>
            <th>
                год рождения автора
            </th>
            {author.map((author) => <AuthorItem author={author} />)}
        </table>
    )
}

export default AuthorList
